!function ($) {
  var pageProducts = function () {

  }

  pageProducts.prototype.init = function () {
    this.createPagination(20, 10)
  }

  pageProducts.prototype.createPagination = function (total, perPage) {
    var self = this;
    $('#pagination-wrapper').twbsPagination({
      totalPages: total % perPage > 0 ? parseInt(total / perPage) + 1 : total / perPage,
      first: '<i class="bx bx-chevrons-left"></i>',
      last: '<i class="bx bx-chevrons-right"></i>',
      prev: '<i class="bx bx-chevron-left"></i>',
      next: '<i class="bx bx-chevron-right"></i>',
      onPageClick: function (event, page) {
        self.getProductByPage(page)
      }
    });
  }

  pageProducts.prototype.getProductByPage = function (pagenum) {
    var self = this;
    if ($.API) {
      $.API.getWithoutToken('product/lists?category&page=' + pagenum).then(function (data) {
        console.log("datta ==>", data)
      }).catch(function () {

      })
    }
  }


  $.pageProducts = new pageProducts, $.pageProducts.Constructor = pageProducts
}(window.jQuery),
  function ($) {
    "use strict";
    $.pageProducts.init();
  }(window.jQuery);
