function addSchema(jsonStr, ldSchema) {
  if (jsonStr.length && typeof jsonStr === 'string') {
    jsonStr = JSON.parse(jsonStr);
    jsonStr.forEach(function (item, index) {
      var obj = {
        "@type": "Product",
        "name": item.name,
        "image": "https://lettstartdesign.com/assets/images/slider-screenshot/" + item.screenshot,
        "description": item.oneLinerDesc,
        "offers": {
          "@type": "Offer",
          "url": "https://lettstartdesign.com/theme/" + item['detailLink'],
          "priceCurrency": "USD",
          "price": item.price
        },
        "aggregateRating": {
          "@type": "AggregateRating",
          "ratingValue": "5",
          "ratingCount": "2"
        }
      }
      ldSchema["@graph"].push(obj);
    });
  }
}

function addArticle (name, desc, ldSchema, feature) {
  var obj = {
    "@type": feature ? "Feature" : "Article",
    "headline": name,
    "datePublished": "2021-02-11",
    "dateModified": "2021-02-11",
    "author": {
      "@type": "Organization",
      "name": "Lettstart Design"
    },
    "publisher": {
      "@id": "https://lettstartdesign.com/#organization"
    },
    "@id": "https://lettstartdesign.com/#richSnippet",
    "isPartOf": {
      "@id": "https://lettstartdesign.com/#webpage"
    },
    "inLanguage": "en-US",
    "mainEntityOfPage": {
      "@id": "https://lettstartdesign.com/#webpage"
    }
  }
  if(desc) {
    obj["description"] = desc
  }
  ldSchema["@graph"].push(obj);
}